package wormholeplugin

import (
	"encoding/json"
	"fmt"
	"log"
	"math/rand"
	"strings"
	"sync"
	"time"

	"github.com/dustin/go-humanize"
	"github.com/iopred/bruxism"
	"github.com/iopred/discordgo"
)

type wormhole struct {
	Webhook string
	Token   string
}

type wormholePlugin struct {
	sync.RWMutex
	Channels      map[string]*wormhole
	PrimeChannels map[string]*wormhole
	Next          map[string]time.Time
	Messages      int
}

// Name returns the name of the plugin.
func (p *wormholePlugin) Name() string {
	return "Wormhole"
}

// Load will load plugin state from a byte array.
func (p *wormholePlugin) Load(bot *bruxism.Bot, service bruxism.Service, data []byte) error {
	if data != nil {
		if err := json.Unmarshal(data, p); err != nil {
			log.Println("Error loading data", err)
		}
	}

	if p.Channels == nil {
		p.Channels = make(map[string]*wormhole)
	}
	if p.PrimeChannels == nil {
		p.PrimeChannels = make(map[string]*wormhole)
	}
	if p.Next == nil {
		p.Next = make(map[string]time.Time)
	}

	return nil
}

// Save will save plugin state to a byte array.
func (p *wormholePlugin) Save() ([]byte, error) {
	return json.Marshal(p)
}

// Help returns a list of help strings that are printed when the user requests them.
func (p *wormholePlugin) Help(bot *bruxism.Bot, service bruxism.Service, message bruxism.Message, detailed bool) []string {
	isAuthorized := service.IsModerator(message)

	if service.Name() == bruxism.DiscordServiceName {
		discord := service.(*bruxism.Discord)
		p, err := discord.UserChannelPermissions(message.UserID(), message.Channel())
		if err == nil {
			isAuthorized = isAuthorized || (p&discordgo.PermissionManageRoles != 0) || (p&discordgo.PermissionManageChannels != 0) || (p&discordgo.PermissionManageServer != 0)
		}
	}

	if isAuthorized {
		if detailed {
			return []string{
				bruxism.CommandHelp(service, "wormhole", "info", "Learn more about wormholes.")[0],
				bruxism.CommandHelp(service, "wormhole", "open", "Opens a wormhole allowing interdimentional communications.")[0],
				bruxism.CommandHelp(service, "wormhole", "close", "Closes the wormhole.")[0],
				bruxism.CommandHelp(service, "wormhole", "send <message>", "Send a message to another dimension.")[0],
			}
		}

		return bruxism.CommandHelp(service, "wormhole", "<info|open|close|send <message>>", fmt.Sprintf("Send messages to other dimensions."))
	}

	if _, ok := p.Channels[message.Channel()]; ok {
		return bruxism.CommandHelp(service, "wormhole", "send <message>", fmt.Sprintf("Send a message to another dimension."))
	}

	return nil
}

// Message handler.
func (p *wormholePlugin) Message(bot *bruxism.Bot, service bruxism.Service, message bruxism.Message) {
	defer bruxism.MessageRecover()
	if !service.IsMe(message) {
		messageChannel := message.Channel()

		if bruxism.MatchesCommand(service, "wormhole", message) {
			ticks := ""
			if service.Name() == bruxism.DiscordServiceName {
				ticks = "`"
			}

			_, parts := bruxism.ParseCommand(service, message)

			if len(parts) == 0 {
				service.SendMessage(messageChannel, fmt.Sprintf("Incorrect command. eg: %s%swormhole send Hello!%s", ticks, service.CommandPrefix(), ticks))
			}

			isAuthorized := service.IsModerator(message)

			if service.Name() == bruxism.DiscordServiceName {
				discord := service.(*bruxism.Discord)
				p, err := discord.UserChannelPermissions(message.UserID(), message.Channel())
				if err == nil {
					isAuthorized = isAuthorized || (p&discordgo.PermissionManageRoles != 0) || (p&discordgo.PermissionManageChannels != 0) || (p&discordgo.PermissionManageServer != 0)
				}
			}

			switch parts[0] {
			case "open":
				if !isAuthorized {
					service.SendMessage(messageChannel, "I'm sorry, you must be the channel owner to open a wormhole.")
					return
				}
				p.Lock()
				defer p.Unlock()
				_, ok := p.Channels[messageChannel]
				if !ok {
					_, ok = p.PrimeChannels[messageChannel]
				}
				if ok {
					service.SendMessage(messageChannel, "A wormhole is already open.")
				} else {
					wormhole := &wormhole{}

					channels := p.Channels
					name := "Wormhole"
					if len(parts) > 1 && parts[1] == "prime" {
						channels = p.PrimeChannels
						name = "Wormhole Prime"
					}

					if service.Name() == bruxism.DiscordServiceName {
						discord := service.(*bruxism.Discord)
						wh, err := discord.Session.WebhookCreate(messageChannel, name, "data:image/jpeg;base64,")
						if err == nil {
							wormhole.Webhook = wh.ID
							wormhole.Token = wh.Token
						} else {
							if len(parts) <= 1 && parts[1] != "legacy" && parts[1] != "prime" {
								service.SendMessage(messageChannel, fmt.Sprintf("Wormholes are way more fun if %s has webhook permissions, please give %s a role with Manage Webhooks. Use %s%swormhole open legacy%s for boring wormholes.", service.UserName(), service.UserName(), ticks, service.CommandPrefix(), ticks))
								return
							}
						}
					}

					channels[messageChannel] = wormhole

					if service.Name() == bruxism.DiscordServiceName && wormhole.Webhook != "" {
						discord := service.(*bruxism.Discord)
						err := discord.Session.WebhookExecute(wormhole.Webhook, wormhole.Token, false, &discordgo.WebhookParams{
							Content: "Wormhole opened!",
						})
						if err != nil {
							service.SendMessage(messageChannel, "A wormhole has been opened.")
						}
					} else {
						service.SendMessage(messageChannel, "A wormhole has been opened.")
					}
				}

			case "close":
				if !isAuthorized {
					service.SendMessage(messageChannel, "I'm sorry, you must be the channel owner to close a wormhole.")
					return
				}
				p.Lock()
				defer p.Unlock()

				channels := p.Channels
				wormhole, ok := channels[messageChannel]
				if !ok {
					channels = p.PrimeChannels
					wormhole, ok = channels[messageChannel]
				}

				if ok {
					if service.Name() == bruxism.DiscordServiceName && wormhole.Webhook != "" {
						discord := service.(*bruxism.Discord)

						err := discord.Session.WebhookExecute(wormhole.Webhook, wormhole.Token, false, &discordgo.WebhookParams{
							Content: "Wormhole closed.",
						})
						if err != nil {
							service.SendMessage(messageChannel, "The wormhole has been closed.")
						}

						discord.Session.WebhookDeleteWithToken(wormhole.Webhook, wormhole.Token)
					} else {
						service.SendMessage(messageChannel, "The wormhole has been closed.")
					}
					delete(channels, messageChannel)
				} else {
					service.SendMessage(messageChannel, "A wormhole has not been opened.")
				}

			case "send":
				wormhole, ok := p.Channels[messageChannel]
				if !ok {
					wormhole, ok = p.PrimeChannels[messageChannel]
				}
				now := time.Now()
				if !ok {
					service.SendMessage(messageChannel, "A wormhole has not been opened yet.")
					return
				} else if !now.After(p.Next[messageChannel]) {
					if service.Name() == bruxism.DiscordServiceName && wormhole.Webhook != "" {
						discord := service.(*bruxism.Discord)
						err := discord.Session.WebhookExecute(wormhole.Webhook, wormhole.Token, false, &discordgo.WebhookParams{
							Content: fmt.Sprintf("Wormhole is busy, available %s.", humanize.Time(p.Next[messageChannel])),
						})
						if err != nil {
							service.SendMessage(messageChannel, fmt.Sprintf("Wormhole is busy, available %s.", humanize.Time(p.Next[messageChannel])))
						}
					} else {
						service.SendMessage(messageChannel, fmt.Sprintf("Wormhole is busy, available %s.", humanize.Time(p.Next[messageChannel])))
					}

					return
				}
				p.Next[messageChannel] = now.Add(1 * time.Minute)

				p.Lock()
				defer p.Unlock()
				r := rand.Intn(len(p.Channels))
				i := 0
				content := strings.Join(parts[1:], " ")
				for channel, wormhole := range p.Channels {
					if i == r {
						if channel != messageChannel {
							p.broadcast(bot, service, message, channel, wormhole, content)
						}
						break
					}
					i++
				}

				for channel, wormhole := range p.PrimeChannels {
					if channel != messageChannel {
						p.broadcast(bot, service, message, channel, wormhole, content)
					}
				}

				p.Messages++
			case "info":
				p.RLock()
				defer p.RUnlock()
				service.SendMessage(messageChannel, fmt.Sprintf("Wormholes are interdimentional links to other channels that %s is in.\nSend messages through wormholes and they will exit from another random wormhole.\nThere are %v wormholes open and %v messages have been sent through wormholes. Visit Wormhole Prime where all messages also appear: https://discord.gg/HQurPde", service.UserName(), len(p.Channels), p.Messages))
			}
		}
	}
}

func (p *wormholePlugin) broadcast(bot *bruxism.Bot, service bruxism.Service, message bruxism.Message, channel string, wormhole *wormhole, content string) {
	if service.Name() == bruxism.DiscordServiceName && wormhole.Webhook != "" {
		discord := service.(*bruxism.Discord)
		err := discord.Session.WebhookExecute(wormhole.Webhook, wormhole.Token, false, &discordgo.WebhookParams{
			Embeds: []*discordgo.MessageEmbed{
				{
					Color:       rand.Intn(0xFFFFFF),
					Description: content,
					Author: &discordgo.MessageEmbedAuthor{
						Name:    message.UserName(),
						IconURL: message.UserAvatar(),
					},
				},
			},
		})
		if err != nil {
			service.SendMessage(channel, fmt.Sprintf("*A message came through the wormhole.*\n%s: %s", message.UserName(), content))
		}
	} else {
		if service.SupportsMultiline() {
			service.SendMessage(channel, fmt.Sprintf("*A message came through the wormhole.*\n%s: %s", message.UserName(), content))
		} else {
			service.SendMessage(channel, fmt.Sprintf("A message came through the wormhole. %s: %s", message.UserName(), content))
		}

	}
}

// New will create a new wormhole plugin.
func New() bruxism.Plugin {
	return &wormholePlugin{
		Channels:      make(map[string]*wormhole),
		PrimeChannels: make(map[string]*wormhole),
		Next:          make(map[string]time.Time),
	}
}
